if (SERVER) then		SWEP.Weight = 0	SWEP.AutoSwitchTo = false	SWEP.AutoSwitchFrom = falseelseif (CLIENT) then	SWEP.Author = "Norra"	SWEP.Purpose = ""	SWEP.Instructions = "Left click to attack."	SWEP.PrintName = "SCP-3114"	SWEP.Slot = 2	SWEP.SlotPos = 0	SWEP.DrawAmmo = false	SWEP.DrawCrosshair = trueendSWEP.AccurateCrosshair = falseSWEP.Category = "Norra's SWEPs"SWEP.Spawnable = trueSWEP.AdminSpawnable = trueSWEP.ViewModel = Model("models/weapons/c_arms.mdl")SWEP.WorldModel = ""SWEP.Primary.ClipSize = -1SWEP.Primary.DefaultClip = -1SWEP.Primary.Automatic = trueSWEP.Primary.Damage = 70SWEP.Primary.Delay	= 1.5SWEP.Primary.Ammo = ""SWEP.Secondary.ClipSize = -1SWEP.Secondary.DefaultClip = -1SWEP.Secondary.Automatic = falseSWEP.Secondary.Ammo = ""SWEP.AnimDelay = 0;SWEP.HitSound	= {	"npc/headcrab_poison/ph_wallhit1.wav",	"npc/headcrab_poison/ph_wallhit2.wav"	};	SWEP.MissSound 	= {	"weapons/iceaxe/iceaxe_swing1.wav",	"npc/vort/claw_swing1.wav",	"npc/vort/claw_swing2.wav"	};	SWEP.SwingSound	= {	"npc/zombie/claw_miss1.wav",	"npc/zombie/claw_miss2.wav"	};	SWEP.FleshSound	= {	"physics/flesh/flesh_squishy_impact_hard1.wav",	"physics/flesh/flesh_squishy_impact_hard2.wav",	"physics/flesh/flesh_squishy_impact_hard3.wav",	"physics/flesh/flesh_squishy_impact_hard4.wav"	};	function SWEP:Initialize()        self:SetHoldType("normal")endfunction SWEP:PrimaryAttack()		self.AnimDelay = CurTime() + 0.5	self:SetNextPrimaryFire( CurTime() + self.Primary.Delay )	self:SetNextSecondaryFire( CurTime() + self.Primary.Delay )		local owner = self.Owner	local tr, trace	local target = owner:GetEyeTrace().Entity		owner:DoAnimationEvent(ACT_GMOD_GESTURE_RANGE_ZOMBIE_SPECIAL)		timer.Simple( 0.5, function()			if SERVER then			owner:EmitSound(table.Random(self.SwingSound))			if !IsValid( target ) then				return			end			target:SetLaggedMovementValue( 0.5 )			timer.Simple(3, function()				target:SetLaggedMovementValue( 1 )			end)		end		tr = {}		tr.start = owner:GetShootPos()		tr.endpos = owner:GetShootPos() + ( owner:GetAimVector() * 95 )		tr.filter = owner		tr.mask = MASK_SHOT		trace = util.TraceLine( tr )		if ( trace.Hit ) then			if trace.Entity:IsPlayer() then				self.Weapon:SendWeaponAnim(ACT_VM_HITCENTER)				bullet = {}				bullet.Num    = 1				bullet.Src    = owner:GetShootPos()				bullet.Dir    = owner:GetAimVector()				bullet.Spread = Vector(0, 0, 0)				bullet.Tracer = 0				bullet.Force  = 1				bullet.Damage = self.Primary.Damage				owner:FireBullets(bullet) 				self.Weapon:EmitSound(table.Random(self.FleshSound),75)			else				self.Weapon:SendWeaponAnim(ACT_VM_HITCENTER)				bullet = {}				bullet.Num    = 1				bullet.Src    = owner:GetShootPos()				bullet.Dir    = owner:GetAimVector()				bullet.Spread = Vector(0, 0, 0)				bullet.Tracer = 0				bullet.Force  = 1				bullet.Damage = self.Primary.Damage				owner:FireBullets(bullet) 					self.Weapon:EmitSound(table.Random(self.HitSound),75)			end		else			self.Weapon:EmitSound(table.Random(self.MissSound),75)		end				timer.Simple( 0.06, function() 			owner:ViewPunch( Angle( -2, -2,  0 ) ) 		end )				timer.Simple( 0.23, function() 			owner:ViewPunch( Angle(  3,  1,  0 ) ) 		end )			end)endfunction SWEP:SecondaryAttack()		returnendfunction SWEP:Think()	local owner = self.Owner	local t = CurTime()	local primary = ( self:GetNextPrimaryFire() - CurTime() )		isSCP3114 = true	if isSCP3114 == true then			owner:SetRunSpeed(105)	end	if self.AnimDelay > t then		return	end		if t + primary > t then		self.AnimDelay = primary + 1.5		return true	elseif owner:IsOnGround() then		owner:DoAnimationEvent(ACT_HL2MP_WALK_ZOMBIE_01)		self.AnimDelay = t + 1.5		return true	end			self.AnimDelay = t + 0.5end		