AddCSLuaFile("shared.lua");AddCSLuaFile("cl_init.lua");include("shared.lua");util.AddNetworkString("c016screen");util.AddNetworkString("c016contain");local mdl = {	"models/props_lab/reciever01c.mdl",	"models/props_lab/jar01a.mdl",	"models/props_junk/MetalBucket01a.mdl",	"models/props_lab/binderredlabel.mdl",	"models/props_junk/plasticbucket001a.mdl",	"models/props_junk/garbage_milkcarton001a.mdl",	"models/props_interiors/pot01a.mdl",	"models/props_combine/breenglobe.mdl",	"models/props_c17/tv_monitor01.mdl",	"models/props_c17/SuitCase001a.mdl",	"models/props_c17/metalPot001a.mdl",	"models/props_c17/chair_office01a.mdl",	"models/props_junk/garbage_coffeemug001a.mdl",}function ENT:Initialize()	self:SetModel(mdl[math.random(1, #mdl)]);	self:PhysicsInit(SOLID_VPHYSICS);	self:SetMoveType(MOVETYPE_VPHYSICS);	self:SetSolid(SOLID_VPHYSICS);	self:SetUseType(SIMPLE_USE);	self:SetTrigger(true);	local physObj = self:GetPhysicsObject();	if (IsValid(physObj)) then		physObj:Wake();		physObj:EnableMotion(true);	end;end;function ENT:RunInfection(ply)	if !(ply.infected) then		ply.infected = !ply.infected		ply:StripWeapons();		ply:SetModel("models/player/zombie_fast.mdl")		ply:updateJob("016-2")		ply:Give("weapon_fists")		ply:ConCommand("simple_thirdperson_enabled 1")		ply:SetHealth(250)		ply:EmitSound("npc/zombie/zombie_pain6.wav", ply:GetPos() )		net.Start("c016screen")		net.WriteBool(true)		net.Send(ply)		DarkRP.notify(ply, 1, 2, "You have been infected by the anomaly (SCP-016)!");	endendlocal hazmats = {	"Hierarchy",	"Staff on Duty",	"Hazmat Unit",	"SCP-096",	"SCP-173",	"SCP-049",	"SCP-087-II",	"SCP-457",	"SCP-035",	"SCP-008-II",	"SCP-106",	"SCP-076-II",	"SCP-999",	"SCP-1370",	"SCP-939",	"SCP-913",	"SCP-682",	"SCP-1048",	"MTF E-11 Containment",	"MTF E-11 Commander",	"MTF Tau-5 Unit",		"Sarkic Cultist",		"Sarkic Karcist",		"The Oneiroi Collective",		"GOC Operative",	"Anderson Robotics Peregrine Droid"}	local jobs = {		"SCP-049-2",		"SCP-008-II",		"Broken Church Convert",		"Chaos Insurgency Alpha",		"Anomaly",		"016-2",		"Werewolf",		"SCP-2191-2",		"SCP-2191-3",		"Nobody",	};function ENT:Use(player)	if table.HasValue( hazmats, team.GetName( player:Team() ) ) then return end;		if ( table.HasValue( jobs, player:getDarkRPVar("job" ) ) ) then		return	end	if ( player.isHazmat == true ) then		return;	end	if self.Disable then	else		self:RunInfection(player);	end;end;function ENT:Think()end;function ENT:StartTouch(entity)	if (!IsValid(entity)) then		return;	end;	if (!entity:IsPlayer()) then		return;	end;	if table.HasValue( hazmats, team.GetName( entity:Team() ) ) then		return;	end;	if ( entity.isHazmat == true ) then		return;	end	if self.Disable then	else		self:RunInfection(entity);	end;end;hook.Add("PlayerDeath", "stupididentifier", function(v, i, a)	if v:IsPlayer() and v.infected then		v.infected = !v.infected;		net.Start("c016screen");		net.WriteBool(false);		net.Send(v);	end;end)function ENT:OnTakeDamage(dmg)	if dmg:GetDamageType() == DMG_BURN and self:IsValid() and !(self.Disable) then		self.Disable = true;		self:EmitSound("npc/zombie/zombie_pain1.wav", self:GetPos());		self:SetColor(Color(145,145,145));		timer.Simple( 300 ,function() if self:IsValid() then self.Disable = false;  self:SetColor(Color(255,255,255)); end; end);	end;end;