AddCSLuaFile("shared.lua")AddCSLuaFile("cl_init.lua")include("shared.lua")function ENT:Initialize()	self:SetModel("models/maxofs2d/hover_rings.mdl")	self:SetMaterial("models/props_combine/portalball001_sheet")	self:PhysicsInit(SOLID_VPHYSICS)	self:SetMoveType(MOVETYPE_VPHYSICS)	self:SetSolid(SOLID_VPHYSICS)	self:SetUseType(SIMPLE_USE)	self:SetColor( Color( 220, 220, 220, 255 ) ) 	self:SetRenderMode( RENDERMODE_TRANSCOLOR )	local physObj = self:GetPhysicsObject()	if (IsValid(physObj)) then		physObj:Wake()		physObj:EnableMotion(true)	end;end;function ENT:Use(activator, ply)	if ( ply.isHazmat == true ) then		return	end	local teams = {			"Hierarchy",		"Staff on Duty",		"Hazmat Unit",				"Broken Church Construct",		"???",		"SCP-096",		"SCP-049",		"SCP-939",		"SCP-173",		"SCP-457",				"SCP-682",				"SCP-076-II",		"SCP-106",		"SCP-999",		"SCP-1370",		"SCP-913",		"SCP-087-II",		"SCP-035",		"SCP-008-II",		"Serpent's Hand",		"MTF Tau-5 Unit",		"Enraged",	};		local jobs = {		"SCP-049-2",		"SCP-008-II",		"Anomaly",				"Sarkic Abomination",				"Nobody",		"Enraged",	};	if ( table.HasValue(teams, team.GetName( ply:Team() ) ) ) then			ply:EmitSound("npc/roller/code2.wav")		DarkRP.notify(ply, 1, 4, "The artifact's properties have no effect on you.");				        return false    end		if ( table.HasValue( jobs, ply:getDarkRPVar("job" ) ) ) then				ply:EmitSound("npc/roller/code2.wav")		DarkRP.notify(ply, 1, 4, "The artifact's properties have no effect on you.");				return false	end		local powers = {				"swep_psychicvision",		"swep_cloak",		"swep_speedy",		"swep_pyro",		"swep_shapeshifter",		"swep_shrink",				"m9k_damascus",		"swep_regenrator"	};		local selectedweapon = table.Random(powers);	local selectedweapon2 = table.Random(powers);		local chance = math.random( 1, 100 )		ply:Give(selectedweapon, false);		ply:EmitSound("npc/combine_gunship/ping_search.wav", 80, 100);		if chance >= 75 then						ply:Give(selectedweapon2, false)					end		DarkRP.notify(ply, 1, 4, "You have been infected by the anomaly and now posses anomalous abilities!");		SafeRemoveEntity(self)	end